void-dancertroupe.card:
position: relative;
    margin-bottom: 20px;
    width: 3in;
    height: 4in;
    border-width: 8px;
    border-style: solid;
    border-image: linear-gradient(152deg, rgba(255,164,0,1) 4%, rgba(0,247,218,1) 41%, rgba(245,0,255,1) 94%) 1;
    background: radial-gradient(circle, rgba(255,255,255,1) 20%, rgba(234,234,234,1) 100%);
    z-index: 0;

void-dancertroupe.card-back:
position: relative;
    margin-bottom: 20px;
    width: 3in;
    height: 4in;
    border-width: 8px;
    border-style: solid;
    border-image: linear-gradient(180deg, rgba(0,0,0,1), rgba(0,0,0,1)) 1;
    background: radial-gradient(circle, rgba(255,164,0,1) 4%, rgba(0,247,218,1) 41%, rgba(245,0,255,1) 94%);
    z-index: 0;

void-dancertroupe.card-image:
position: absolute;
    margin: auto;
    width: .8in;
    height: .8in;
    top: 3in;
    left: 1.1in;
    opacity: .2;
    background-color: transparent;
    background-image: url(http://eldar.arhicks.co.uk/graphics/runes/plain/masque_of_the_twisted_path_rune_1.png);
    background-size: cover;
    background-position: center center;
    z-index: -1;

void-dancertroupe.title:
height: .5in;
    padding-top: 5px;
    background: radial-gradient(circle, rgba(195,125,0,1) 4%, rgba(0,179,158,1) 41%, rgba(142,0,148,1) 94%);

void-dancertroupe.stat-name:
font-family: Arial;
    font-weight: bold;
    text-align: center;
    color: white;
